import React, { useState } from 'react'
import ReactDOM from 'react-dom'

import {FiPlusCircle} from 'react-icons/fi'
import NewProjectModal from '../../modal/NewProjectModal'
import InfiniteList from '../../util/InfiniteList'
import Modal from '../../util/Modal'

import ProjectCard from './ProjectCard'

import styles from './ProjectFolder.module.css'

const ProjectFolder = ({projects, name, showAdd}) => {

    const [isShowNewModal, setShowNewModal] = useState(false);

    return (
        <div className={styles.container}>
            <div className={styles.heading}>
                <div className={styles.title}>{name}</div>
                <div className={styles.icon_wrapper}>@</div>
            </div>
            <div id='inf_projects' className={styles.content}>
                {showAdd && <div onClick={()=>setShowNewModal(true)} className={styles.add_card}><FiPlusCircle/></div>}
                <InfiniteList list={projects} elem='inf_projects' api='project/own' action='GET_OWN_PROJECTS'>
                    {projects.map(project => (
                        <ProjectCard key={project._id} project={project} />
                    ))}
                </InfiniteList>     
                {/* {projects.map(project => (
                    <ProjectCard key={project._id} project={project} />
                ))} */}
            </div>
            {isShowNewModal && ReactDOM.createPortal(
            <Modal setIsEdit={setShowNewModal} width='30%' >
                <NewProjectModal setIsEdit={setShowNewModal} />
            </Modal>
            , portal)}
        </div>
    )
}

export default ProjectFolder
