import React, { useEffect, useState } from 'react'
import styles from './NotificationCount.module.css'
import RoundWrapper from '../util/RoundWrapper'
import { useSelector } from 'react-redux'

export default function NotificationCount(props) {
    const {notice} = useSelector(state=>state);
    const [notiLength, setNotiLength] = useState();

    useEffect(()=>{
        const readNotices = notice.data.filter(noti=>!noti.isRead);
        setNotiLength(readNotices.length)
    },[notice])

    return (
            <RoundWrapper Func={props.Func} diameter='2.5rem' bgcolor={props.bgcolor} color={props.color}>
                {notiLength}
            </RoundWrapper>
    )
}
