import React, { useEffect, useRef, useState } from 'react';
import {useDispatch, useSelector} from 'react-redux';
import styles from './Search.module.css';
import {AiOutlineSearch} from 'react-icons/ai';
import { getAPI } from '../../apis/Axios';
import { CSSTransition } from "react-transition-group";
import UserCard from './UserCard';

const Search = () => {
    const dispatch = useDispatch();
    const [search,setSearch] = useState('');
    const [users,setUsers] = useState([]);
    const [isShown,setIsShown] = useState(true); 
    const auth = useSelector(state=>state.auth);
    const [height2, setHeight] = useState(0);

    let height = users.length * 50;
    const ref = useRef();

    const submitHandler = async (e) => {
        e.preventDefault();

        if(!search) return;
        if(auth.token && search){
            try {
                const res = await getAPI(`user/search?username=${search}`, auth.token);
                setUsers(res.data.user);

            } catch (err) {
                dispatch({
                type: 'ALERT', payload: {error: err.response.data.msg}
            })
            }   
        }
    }
    
    const inputHandler = e => setSearch(e.target.value);
    const onChangeHandler = e => {
        inputHandler(e);
        submitHandler(e);
        setHeight(ref.current.clientHeight);

    }
    const resetHandler = () => {

    }

    return (
        <form onSubmit={submitHandler} className={styles.search_bar}>
            <div className={styles.input_wrapper}>
                <label className={styles.icon_wrapper} htmlFor="seacrh"><AiOutlineSearch className={styles.icon}/></label>
                <input onChange={onChangeHandler} autoComplete="off" value={search}  className={styles.input} type="text" name="seacrh" id="seacrh" placeholder="Search for people, jobs, companies and more..."/>
                <button style={{display: 'none'}} type="submit" value="" ></button>
            </div>
            {/*  */}
            <CSSTransition
            in={search}
            timeout={400}
            classNames={{
                enter: styles.enter,
                enterActive: styles.enter_active,
                exit: styles.exit,
                exitActive: styles.exit_active,
            }}
            unmountOnExit
            >
                <div style={{height: `${height}px`, backgroundColor:'red',transition:'all 1s'}} >
                    <div ref={ref} className={styles.list_items}>
                        {  users.map(user => 
                            <UserCard height={'50'} key={user.id} username={user.username} />
                        )}
                    </div>
                </div> 
            </CSSTransition>
              {/*  */}
              {height2}
        </form>
    )
}

export default Search
