import React, { useState } from 'react'

import {FaCheck} from 'react-icons/fa'
import {ImCross} from 'react-icons/im'
import { useDispatch } from 'react-redux'

import styles from './PasswordModal.module.css'

const PasswordModal = ({setChangePassword}) => {
    const dispatch = useDispatch();
    const passwordRegex = new RegExp('^(?=.*[A-Za-z])(?=.*\d)[A-Za-z\d]{6,}$');

    const [requirement, setRequirement] = useState({
        length: false,
        hasNumber: false
    })

    const [passwordForm, setPasswordForm] = useState({
        oldPw:'',
        newPw: '',
        newPw2: ''
    });

    const [error, setError] = useState(false);

    const closeModal = () => {
        dispatch({type: 'MODAL', payload: false});
        setChangePassword(false);
    }

    const inputHandler = e => setPasswordForm({...passwordForm,[e.target.name]: e.target.value});

    const checkHandler = () => {
        if(passwordForm.newPw) {
            passwordForm.newPw.length >= 6 ? setRequirement({...requirement, length: true}) : setRequirement({...requirement, length: false});
            passwordForm.newPw.search(/\d/) !== -1 ? setRequirement({...requirement, hasNumber: true}) : setRequirement({...requirement, hasNumber: true});
        }

        passwordForm.newPw2 && (passwordForm.newPw !== passwordForm.newPw2 && console.log('pw ko giong nhau'))
    }

    const submitHandler = () => {
        
    }

    return (
        <div className={styles.container}>
            <div className={styles.side}>
                <div className={styles.title}>Change Password</div>
                <div className={styles.text}>Passwords must contain:</div>
                <div className={styles.requirements}>
                    <div className={styles.requirement_wrapper}>
                        <div className={styles.icon_wrapper}>{requirement.length ? <FaCheck style={{color: '#48bf91'}} /> : <ImCross style={{color: '#e7727d'}} />}</div>                  
                        <div style={{textDecoration: `${requirement.length?'line-through':''}`}} className={styles.requirement}>At least 6 characters</div>
                    </div>
                    <div className={styles.requirement_wrapper}>
                        <div className={styles.icon_wrapper}>{requirement.hasNumber ?  <FaCheck style={{color: '#48bf91'}} /> : <ImCross style={{color: '#e7727d'}} />}</div>                  
                        <div style={{textDecoration: `${requirement.hasNumber?'line-through':''}`}} className={styles.requirement}>At least 1 number (0-9)</div>
                    </div>
                </div>
            </div>
            <div className={styles.side}>
                <div className={styles.input_container}>
                    <div className={styles.input_wrapper}><input value={passwordForm.oldPw} name='oldPw' onChange={inputHandler} onBlur={checkHandler} type="password" className={styles.input} placeholder='Input old password...' /></div>
                    <div className={styles.input_wrapper}><input value={passwordForm.newPw} name='newPw' onChange={inputHandler} onBlur={checkHandler} type="password" className={styles.input} placeholder='Input new password...' /></div>
                    <div className={styles.input_wrapper}><input value={passwordForm.newPw2} name='newPw2' onChange={inputHandler} onBlur={checkHandler} type="password" className={styles.input} placeholder='Confirm new password...' /></div>
                </div>
                <div className={styles.button_container}>
                    <div className={styles.button}><button className={`${styles.btn} ${styles.save}`}>SAVE</button></div>
                    <div onClick={closeModal} className={styles.button}><button className={`${styles.btn} ${styles.cancel}`}>Cancel</button></div>
                </div>
            </div>
        </div>
    )
}

export default PasswordModal
