import React from 'react'
import { useDispatch, useSelector } from 'react-redux';
import styles from './OptionList.module.css'

const OptionList = ({setIsEdit, post}) => {
    const dispatch = useDispatch();
    const {auth, profile} = useSelector(state=>state);

    const deleteHander = () => {
        dispatch(deletePost(auth, post._id, profile));
    }

    return (
        <div className={styles.option_list}>
            <div className={styles.option_item} onClick={()=>console.log('dog')} >
                <MdSave className={styles.icon}/>
                Save
            </div>
            <div className={styles.option_item} onClick={()=>setIsEdit(true)}>
                <MdModeEdit className={styles.icon}/>
                Edit
            </div>
            <div className={styles.option_item} onClick={deleteHander} >
                <RiDeleteBinLine className={styles.icon}/>
                Delete
            </div>
        </div>
    )
}

export default OptionList
