import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { follow, unfollow } from '../../store/actions/profileAction';
import styles from './FollowBtn.module.css';

const FollowBtn = (props) => {
    const dispatch = useDispatch();
    const [followed, setFollowed] = useState(false);
    const [load, setLoad] = useState(false)

    const auth = useSelector(state=>state.auth);
    const profile = useSelector(state=> state.profile);

    useEffect(()=>{
        if(auth.user.following.find(item => item._id === props.user._id)){
            setFollowed(true);
        }
        else return () => setFollowed(false)
    
    },[auth.user.following, profile.user._id])

    const followHandler = async () => {
        if(load) return;
        setLoad(true)
        await dispatch(follow(auth, profile.user));    
        setFollowed(true);
        setLoad(false)
    }

    const unfollowHandler = async () => {
        if(load) return;
        setLoad(true)
        await dispatch(unfollow(auth, profile.user));
        setFollowed(false);
        setLoad(false)
    }
    return (
        <>
        {followed ? 
            <button className={`${styles.btn} ${styles.follow_btn}`} onClick={unfollowHandler} > Unfollow </button> :
            <button className={`${styles.btn} ${styles.unfollow_btn}`} onClick={followHandler} > Follow </button>
        }
        </>     
    )
}

export default FollowBtn
