const initialState = {
    users: [],
    loading: false
}

const suggestionReducer = (state=initialState, action) => {
    switch (action.type) {
        case 'LOADING':
            return {
                ...state,
                loading: action.payload
            }
        case 'GET_SUGGESTION':
            return {
                ...state,
                users: action.payload.users,
            }
        default: return state;
    }
}
