import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'

import {getPosts} from '../../store/actions/postAction'
import {getSuggestion} from '../../store/actions/suggestionAction'

import NavList from '../../components/navigation/NavList'
import Header from '../../components/header/Header'
import Status from '../../components/home/Status'
import PostCard from '../../components/post/PostCard'
import RightSideBar from '../../components/home/RightSideBar'

import Spinner from '../../assets/Spinner.png'
import styles from './Home.module.css';
import InfiniteList from '../../components/util/InfiniteList'

export default function Home() {
    const dispatch = useDispatch();
    const {auth, post} = useSelector(state=>state);

    const [postShow, setPostShow] = useState([]);

    useEffect(()=>{
        dispatch(getPosts(auth));
        dispatch(getSuggestion(auth));
    },[auth])

    

    return (
        <>
            <Header/>
            <div className={styles.content}>
                <div className={styles.container}>
                    <div className={styles.leftside}>
                        <div className={styles.nav_list_wrapper}>
                            <NavList/>
                        </div>         
                    </div>
                    <div className={styles.container_2}>
                        <div className={styles.middle}>
                            <Status/>
                            {post.loading?
                            <div className={styles.spinner_wrapper}>
                                <img src={Spinner} alt='spinner' />
                            </div>                      
                            :
                            // (post.posts.length>0 ?  post.posts.map(post=>(
                            //     <PostCard key={post._id} post={post} />
                            // ))
                            <InfiniteList postShow={postShow} setPostShow={setPostShow} />
                            // :
                            // <div>No post</div>
                            }                       
                        </div>
                        <div className={styles.rightside}>
                            <RightSideBar/>           
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}
