import React, {useEffect, useState } from 'react'
import {useSelector, useDispatch} from 'react-redux'
import Logo from '../../../assets/logo.png'
import RoundWrapper from '../../util/RoundWrapper'
import {createComment} from '../../../store/actions/commentAction'
import ContentEditable from 'react-contenteditable'
import styles from './InputComment.module.css'

const InputComment = ({onReply, setOnReply, post}) => {
    const dispatch = useDispatch();
    const [body, setBody] = useState('');
    const [onSubmit, setOnSubmit] = useState(false);
    const {auth} = useSelector(state=>state);
    const posts = useSelector(state=>state.post.posts);

    useEffect(()=>{
        if(onSubmit) submitHandler();
    },[onSubmit])

    // useEffect(()=>{
    //     if(onReply) setBody(``);
    // },[body])

    const submitHandler = () => {
        const trimBody = body.trim();
        if(trimBody=='') return;
        const newComment = {
            body: trimBody,
            user: auth.user,
            createdAt: new Date().toISOString(),
            reply: onReply && (onReply.reply ? onReply.reply : onReply._id),
            tag: onReply && onReply.user
        }
        dispatch(createComment(auth, newComment, post, posts));
        setBody('');
    }

    const inputHandler = (e) => {
        if(e.charCode == 13) {
            e.preventDefault();
            setOnSubmit(true);
        }
        if(e.charCode == 64) {
            e.preventDefault();
            setBody('tag ne')
        }
    }
    
    return (
        <form onSubmit={submitHandler} className={styles.container}>
            <div className={styles.avatar}>
                <RoundWrapper diameter='50px' >
                    <img src={auth.user.avatar} />
                </RoundWrapper>
            </div>
            <div className={styles.input_wrapper}>
                <ContentEditable data-placeholder='Write your comment here...' className={styles.input} onKeyPress={inputHandler} spellCheck='false' onChange={e=>setBody(e.target.value)} html={body} tagName="label" />       
            </div>
        </form>
    )
}

export default InputComment
