import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux';


const SocketClient = () => {
    const {auth, socket} = useSelector(state=>state);
    const dispatch = useDispatch();

    // join user
    useEffect(()=>{
        socket.emit('joinUser', auth.user);
    },[socket, auth.user]);

    // like
    useEffect(()=>{
        socket.on('likeToClient', newPosts=>{
            dispatch({type: 'UPDATE_POST', payload: newPosts});
        });
        return () => socket.off('likeToClient');
    },[socket, dispatch]);

    // unlike
    useEffect(()=>{
        socket.on('unlikeToClient', newPosts=>{
            dispatch({type: 'UPDATE_POST', payload: newPosts});
        });
        return () => socket.off('unlikeToClient');
    },[socket, dispatch]);

    // comment
    useEffect(()=>{
        socket.on('commentToClient', newPosts=>{
            dispatch({type: 'UPDATE_POST', payload: newPosts});
        });
        return () => socket.off('commentToClient');
    },[socket, dispatch])

    // follow
    useEffect(()=>{
        socket.on('followToClient', newUser =>{
            dispatch({type: 'AUTH', payload: {...auth, user: newUser}});
        });
        return () => socket.off('followToClient');
    },[socket, dispatch])

    return (
        <>
            
        </>
    )
}

export default SocketClient
