import React, { useState } from 'react'
import CommentCard from './CommentCard'
import InputComment from './InputComment'

const CommentWrapper = ({post, comment, reply}) => {
    const [onReply, setOnReply] = useState(false);

    return (
        <div>
            <CommentCard setOnReply={setOnReply} post={post} comment={comment} />
            <CommentCard post={post} comment={reply} />
            {onReply && <InputComment post={post} onReply={onReply} setOnReply={setOnReply} />}
        </div>
    )
}

export default CommentWrapper
