import React from 'react'
import styles from './LoginForm.module.css'
import {AiOutlineUser,AiOutlineLock} from "react-icons/ai";
import RoundButton from '../Util/RoundButton';
import {Link} from 'react-router-dom';
import axios from 'axios';
import {useEffect,useState} from 'react';

export default function LoginForm() {
    var bodyFormData = new FormData();
    bodyFormData.append('username','minh123');
    bodyFormData.append('password','123123');

    useEffect(()=>{
        getPosts(bodyFormData);
    },[])
    
        const getPosts = async (loginForm) => {
            // e.preventDefault();
                try {
                    const res = await axios.post('http://localhost:5000/api/auth/login',loginForm,{
                        headers:{'Content-type': 'multipart/form-data'}
                    })
                    console.log(res);
                } catch(err) {
                    console.log(err);
                }
            }
    return (
        <div className={styles.form_wrapper}>
            <form className={styles.form} action="http://localhost:5000/api/auth/login" method="post">
            <div className={styles.title}>Sign in to DevUp</div>
            <div className={styles.inputs_wrapper}>
                <div className={styles.input_wrapper}>
                    <label className={styles.icon_wrapper} htmlFor="username"><AiOutlineUser className={styles.icon}/></label>
                    <input className={styles.input} type="text" name="username" id="username" placeholder="Username"/>
                </div>
                <div className={styles.input_wrapper}>
                    <label className={styles.icon_wrapper} htmlFor="password"><AiOutlineLock className={styles.icon}/></label>
                    <input className={styles.input} type="text" name="password" id="password" placeholder="Password"/>
                </div>
            </div>  
            <div className={styles.util_inline}>
                <div className={styles.input_radio_wrapper}>
                    <input type="checkbox" name="remember" id="remember" />
                    <label className={styles.remember} htmlFor="remember">Remember me</label>
                </div>
                <Link to="/forgot" className={styles.forgot}>Forgot Password?</Link>
            </div>
            <RoundButton Func={getPosts} type={'submit'} color={'white'} bgcolor={'#487fec'} value={'Sign in'} />
            </form> 
        </div>
    )
}
