import React, { useState, useEffect } from 'react'
import CommentCard from './CommentCard'
import styles from './Comment.module.css'

const Comment = ({post}) => {
    const [mainCmt, setMainCmt] = useState([]);
    const [replyCmt, setReplyCmt] = useState([]);
    const [showCmt, setShowCmt] = useState([]);
    const [cmtShowNumber, setCmtShowNumber] = useState(3);

    useEffect(()=>{
        const cmt = post.comments.filter(cmt => !cmt.reply);
        setMainCmt(cmt);
        setShowCmt(cmt.slice(cmt.length - cmtShowNumber));
    },[post.comments, cmtShowNumber]);
    
    return (
        <div className={styles.container}>
            {showCmt.map((cmt, index) => (
                <CommentCard key={index} comment={cmt} post={post} />
            ))}
            {mainCmt.length - cmtShowNumber > 0 ?
            <div onClick={setCmtShowNumber(cmtShowNumber + 10)} className={styles.toggle_cmt}>
                See more comments...
            </div> :
            <div onClick={setCmtShowNumber(3)} className={styles.toggle_cmt}>
                Hide comments...
            </div>
            }
        </div>
    )
}

export default Comment
