import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { useMediaQuery } from 'react-responsive'

import Header from '../../components/header/Header'
import { getPosts } from '../../store/actions/postAction'
import NavList from '../../components/home/left-side/NavList'

import Spinner from '../../assets/Spinner.png'

import styles from './Saved.module.css'
import InfiniteList from '../../components/util/InfiniteList'

const Saved = () => {
    const dispatch = useDispatch()

    const {auth} = useSelector(state=>state)

    const isDesktop = useMediaQuery({ query: '(min-width: 1224px)' })



    return (
        <>
            <Header/>
            <div className={styles.content}>
                <div className={styles.container}>
                    {isDesktop && <div className={styles.leftside}>
                        <NavList/>
                    </div> }
                    <div id='inf_posts' className={styles.middle}>
                        {/* {post.loading?
                        <div className={styles.spinner_wrapper}>
                            <img src={Spinner} alt='spinner' />
                        </div>                      
                        : */}
                        <InfiniteList action='GET_MORE_POSTS' api='post' list={auth.user.saved} elem='inf_posts' >
                            {auth.user.saved.length > 0 ? auth.user.saved.map(post => (
                                // <PostCard key={post._id} post={post} />
                                <div>123</div>
                            ))
                            :
                            <div className={styles.no_post}>No saved post available</div>}
                        </InfiniteList>
                        {/* } */}
                    </div>
                </div>
            </div>
        </>
    )
}

export default Saved
