import LoginForm from "../components/auth/LoginForm";
import RegisForm from "../components/auth/RegisForm";
import "./Auth.css";

const Auth = (props) => {
    return (
        <>
        <div className="body">
            <div className="wrapper">
                <div className="container">
                    <div className="avatar-side">
                        <div className="logo">
                            MEETUP APP
                        </div>
                        <h2 className="title">Đăng nhập gần đây</h2>
                        <small className="small">Nhấp vào ảnh của bạn hoặc thêm tài khoản</small>
                    </div>
                    <div className="form-side">
                        {props.authRoute === 'login' && <LoginForm/>}
                        {props.authRoute === 'register' && <RegisForm/>}
                    </div>
                </div>
            </div>
        </div>
        <div className="footer">
            3
        </div>
        </>
    );
}

export default Auth;
