import React, { useState } from 'react'

import TextareaAutosize from 'react-textarea-autosize'

import styles from './ShareModal.module.css'

const ShareModal = ({post, setIsEdit}) => {

    const [body, setBody] = useState('');

    const submitHandler = () => {
        post.shareContent ? dispatch(sharePost(auth, post.shareContent, socket)) : dispatch(sharePost(auth, post, socket))
    }

    const closeModal = () => {
        dispatch({type: 'MODAL', payload: false});
        setIsEdit(false);
    }

    return (
        <div className={styles.container}>
            <div className={styles.heading}>Sharing</div>
            {/* <div className={styles.usercard}>usercard</div> */}
            <div className={styles.content}>
                <div className={styles.input_wrapper}>
                    <TextareaAutosize minRows='3' maxRows='9' value={body} spellCheck='false' type='text' name='body' onChange={e=>setBody(e.target.value)} className={styles.textarea} placeholder="What's on your mind?" />
                </div>
                <div className={styles.share_content}></div>
            </div>
            <div className={styles.submit_btn}></div>
        </div>
    )
}

export default ShareModal
