import React, { useRef, useState } from 'react'

import Header from '../../components/header/Header'

import { motion } from 'framer-motion'

import Editor from '../../components/code/Editor'

import styles from './Code.module.css'


const Code = () => {
    const constRef = useRef(null);

    const [html, setHtml] = useState('')
    const [css, setCss] = useState('')
    const [js, setJs] = useState('')
    const [srcDoc, setSrcDoc] = useState(
    `<html>
          <body><div>xin chao</div></body>
           <style></style>
           <script></script>
        </html>
    `);

    return (
        <>
            <Header/>
            {/* <motion.div style={{width: 500, height:500}} ref={constRef}>
                <motion.div
                drag
                dragConstraints={constRef}
                style={{width: 'fit-content'}}
                >
                    drag me
                </motion.div>
            </motion.div> */}
            <div className={styles.container}>
                <div className={styles.editors_container}>
                    <Editor
                    language="xml"
                    name="HTML"
                    value={html}
                    onChange={setHtml}
                    />
                    <Editor
                    language="css"
                    name="CSS"
                    value={css}
                    onChange={setCss}
                    />
                    <Editor
                    language="javascript"
                    name="JS"
                    value={js}
                    onChange={setJs}
                    />
                </div>
                <div className={styles.result}>
                    <iframe
                    srcDoc={srcDoc}
                    title="output"
                    sandbox="allow-scripts"
                    frameBorder="0"
                    width="100%"
                    height="100%"
                    />
                </div>
            </div>
        </>
    )
}

export default Code
