import React, { useState } from 'react'
import styles from './ReactBar.module.css'
import {AiTwotoneHeart} from 'react-icons/ai'
import {IoMdShareAlt} from 'react-icons/io'
import {BsFillChatDotsFill} from 'react-icons/bs'
import { useDispatch, useSelector } from 'react-redux'
import { likePost } from '../../store/actions/postAction'


const ReactBar = ({id}) => {
    const dispatch = useDispatch();
    const {auth} = useSelector(state=>state);
    const [liked, setLiked] = useState(false);

    const likeHandler = () => {
        dispatch(likePost(auth,id));
    }

    const unlikeHandler = () => {
        dispatch(unlikePost(auth,id));
    }

    return (
        <div className={styles.react}>
            {liked ?
            <div onClick={unlikeHandler} className={styles.react_btn}>
                <div className={styles.icon_wrapper}><AiTwotoneHeart/></div>
                <div className={styles.label}>Unlike</div>        
            </div>
            :
            <div onClick={likeHandler} className={styles.react_btn}>
                <div className={styles.icon_wrapper}><AiTwotoneHeart/></div>
                <div className={styles.label}>Like</div>        
            </div>
            }
            <div className={styles.react_btn}>
                <div className={styles.icon_wrapper}><BsFillChatDotsFill/></div>
                <div className={styles.label}>Comment</div>            
            </div>
            <div className={styles.react_btn}>
                <div className={styles.icon_wrapper}><IoMdShareAlt/></div>
                <div className={styles.label}>Share</div>         
            </div>
        </div>
    )
}

export default ReactBar
