import React from 'react'
import {useState, useRef, useEffect} from 'react'
import { useSelector} from 'react-redux'
import {Link} from 'react-router-dom'

import {AiOutlineSetting} from 'react-icons/ai'
import {IoSearch} from 'react-icons/io5'

import RoundWrapper from '../../components/util/RoundWrapper'
import Search from './Search'
import NotificationDropdown from '../notification/NotificationDropdown'
import SettingOption from './SettingOption'
import NotificationCount from '../notification/NotificationCount'
import RsMenu from './RsMenu'
import SearchResult from './SearchResult'

import { motion } from "framer-motion"

import Logo from '../../assets/logo.png'
import styles from './RsHeader.module.css'

const RsHeader = () => {
    const ref = useRef();

    const [isShow,setIsShow] = useState({
        setting: false,
        noti: false
    });

    const [showNav, setShowNav] = useState(false)

    const {auth} = useSelector(state=>state);

    useEffect(()=>{
        const checkIfClickOutside = e => {
            if((isShow.setting || isShow.noti) && ref.current && !ref.current.contains(e.target)) setIsShow({setting: false,noti: false});   
        }
        document.addEventListener("mousedown", checkIfClickOutside)
        return () => {
            document.removeEventListener("mousedown", checkIfClickOutside)
        }
    },[isShow]);

    const showSettingToggle = () => {
        if(!isShow.setting) setIsShow({noti: false, setting: true});
        else 
        setIsShow({setting: false, noti: false});
    }

    const showNotiToggle = () => {
        if(!isShow.noti) setIsShow({setting: false, noti: true});
        else setIsShow({setting: false, noti: false});
    }



    return (
        <div className={styles.header}>
            <div className={styles.container}>
                <div className={styles.logo_wrapper}>
                    <Link to={'/'} >
                        <img className={styles.logo} src={Logo} alt='logo'/>
                    </Link>
                </div>               
                {/* <SearchResult/> */}
                {/* <div className={styles.account}>
                    <div className={styles.avatar}>  
                        <Link to={`/profile/${auth.user._id}`} replace>
                            <RoundWrapper diameter='3.5rem'>
                            <img src={auth.user.avatar} alt="avatar"/>
                            </RoundWrapper>
                        </Link>                          
                    </div>
                    <div ref={ref} className={styles.clickoutside_wrapper}>
                        <div className={styles.notification_count}>
                            <NotificationCount Func={showNotiToggle} bgcolor='#5BB7ED' color='white' />
                            <NotificationDropdown isShow={isShow.noti} />
                        </div>          
                        <div className={styles.setting}>
                            <RoundWrapper Func={showSettingToggle} diameter='2.5rem' bgcolor='#EAEAF1'>
                            <motion.div 
                            whileHover={{rotate: 90, scale: 1.1}} 
                            style={{display: 'flex', padding: '1rem', borderRadius: '50%'}}>                    
                                <AiOutlineSetting />
                            </motion.div>
                            </RoundWrapper>           
                            <SettingOption isShow={isShow.setting}  />
                        </div>
                    </div>
                </div> */}
                <div className={styles.btn_group_wrapper}>
                    <div className={styles.btn_group}>
                        <div className={styles.search}>
                            <IoSearch/>
                        </div>
                        <NotificationCount margin='1.2rem' diameter='1.7rem' Func={showNotiToggle} bgcolor='#5BB7ED' color='white' />
                        <div onClick={()=>setShowNav(!showNav)} className={styles.menu_icon_wrapper}>
                            <div className={styles.menu_icon}>
                                <div className={styles.line_1}></div>
                                <div className={styles.line_2}></div>
                                <div className={styles.line_3}></div>

                            </div>
                        </div>
                    </div>
                </div>              
            </div>
            {showNav &&
            <motion.div 
            initial={{transform: 'translate(100%,0)'}}
            animate={{transform: 'translate(0,0)', transitionDuration: `.3s`}}
            exit={{transform: 'translate(100%,0)', transitionDuration: `.3s`, opacity: 0}}
            >
                <RsMenu/>
            </motion.div>}
        </div>
    )
}

export default RsHeader
