import React from 'react'
import {useRef, useEffect} from 'react'

const OutsideHover = (props) => {
    const ref = useRef()

    useEffect(()=>{
        const checkIfHoverOutside = e => {
            if(props.state && ref.current && !ref.current.contains(e.target)){
                setTimeout(()=>props.setState(false), 50)
            } 
        }
        document.addEventListener("mouseenter", checkIfHoverOutside)
        return () => {
            document.removeEventListener("mouseenter", checkIfHoverOutside)
        }
    },[props.state])

    return (
        <div style={{width: '100%', height: '100%'}} ref={ref}>
            {props.children}
        </div>
    )
}

export default OutsideHover
