import React from 'react'
import { useDispatch } from 'react-redux'
import { Link } from 'react-router-dom'

import UserCard from '../util/UserCard'

import {GrFormClose} from 'react-icons/gr'

import FollowBtn from '../profile/FollowBtn'

import styles from './CommentModal.module.css'

const CommentModal = ({comments, setIsEdit}) => {
    const dispatch = useDispatch();
    
    const closeModal = () => {
        dispatch({type: 'MODAL', payload: false});
        setIsEdit(false);
    }

    return (
        <div className={styles.container}>
            <div className={styles.heading}>
                <div className={styles.title}>People comment at this post</div>
                <div onClick={closeModal} className={styles.close}><GrFormClose className={styles.icon} /></div>
            </div>
            <div className={styles.content}>
                {comments.map(comment=>(
                    <div  key={comment.user._id} className={styles.item_wrapper}>
                        <Link className={styles.item} to={`/profile/${comment.user._id}`}>
                            <UserCard height='3rem' color='#64BDF1' avatar={comment.user.avatar} username={comment.user.username} />
                        </Link>
                        <div className={styles.btn_wrapper}>
                            <FollowBtn user={comment.user}/>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    )
}

export default CommentModal
