import {deleteAPI, getAPI, patchAPI, postAPI} from '../../apis/Axios'
import {imageUpload} from '../../apis/Cloudinary'
import {createNotice, deleteNotice} from './noticeAction'

export const createPost = (data, auth, socket) => async (dispatch) => {
    let media = [];
    try {
        dispatch({type: 'ALERT', payload: {loading: true}});
        dispatch({type: 'MODAL' , payload: true});
        if(data.image.length > 0) media = await imageUpload(data.image);

        const res = await postAPI('post', {body: data.body,image: media}, auth.token);
        const newPost = {...res.data.newPost, user: auth.user};
        dispatch({type:'CREATE_POST', payload: newPost});
        dispatch({type: 'MODAL' , payload: false});
        dispatch({type: 'ALERT', payload: {success: res.data.msg}});

        let msg = {
            id: res.data.newPost._id,
            receivers: newPost.user.followers,
            url: res.data.newPost._id,
            body: data.body,
            tag: 'POST',
            image: media.length>0 ? media[0].url : ''
        }
        msg = {...msg, text: `${media.length>0 ? 'add new image' : 'post a status'}`}

        dispatch(createNotice(auth, msg, socket));

    } catch(err) {
        dispatch({type: 'MODAL' , payload: false});
        dispatch({type: 'ALERT', payload: {error: err.response.data.msg}})
    }
}

export const getPosts = (auth) => async (dispatch) => {
    try {
        const res = await getAPI('post', auth.token);
        dispatch({type: 'GET_POSTS', payload: res.data.posts})

    } catch(err) {
        dispatch({type: 'ALERT', payload: {error: err.response.data.msg}})
    }
}

export const getPost = (auth, id) => async (dispatch) => {
    try {
        const res = await getAPI(`post/${id}`, auth.token);
        dispatch({type: 'GET_POST', payload: res.data.post})

    } catch(err) {
        dispatch({type: 'ALERT', payload: {error: err.response.data.msg}})
    }
}

export const updatePost = (content, auth, id, profile) => async (dispatch) => {
    try {
        dispatch({ type: 'ALERT', payload: {loading: true} });
        dispatch({type: 'MODAL' , payload: true});

        const res = await patchAPI(`post/${id}`, {body: content}, auth.token);

        // logic
        let newPosts = profile.posts.map(p=>p._id===id?res.data.newPost:p);

        dispatch({type: 'UPDATE_POST', payload: newPosts});
        dispatch({ type: 'MODAL' , payload: false});
        dispatch({ type: 'ALERT', payload: {success: res.data.msg} });

    } catch(err) {
        dispatch({ type: 'MODAL' , payload: false});
        dispatch({type: 'ALERT', payload: {error: err.response.data.msg}})
    }
}

export const deletePost = (auth, id, profile) => async (dispatch) => {
    const newPosts = profile.posts.filter(post => post._id !== id);
    dispatch({type: 'UPDATE_POST', payload: newPosts});

    try {
        const res = await deleteAPI(`post/${id}`, auth.token);
        dispatch({ type: 'ALERT', payload: {success: res.data.msg} });

        const msg = {
            receivers: auth.user.followers.length>0 && auth.user.followers,
            url: id
        }
        dispatch(deleteNotice(auth, msg));

    } catch(err) {
        dispatch({type: 'ALERT', payload: {error: err.response.data.msg}})
    }
}

export const likePost = (auth, post, profile, socket) => async (dispatch) => {
    // logic
    if(auth.user._id === post.user._id) return dispatch({type: 'ALERT', payload: {warn: 'Dont like your own post! '}});
    const newPost = {...post, likes: [...post.likes, auth.user]};
    let newPosts = profile.posts.map(p=>p._id===post._id?newPost:p);

    dispatch({type: 'UPDATE_POST', payload: newPosts});
    socket.emit('likePost', newPost, newPosts);

    const msg = {
        id: auth.user._id,
        receivers: auth.user.followers,
        text: 'like your post!',
        url: newPost._id,
        tag: 'LIKE',
        body: '',
        image: newPost.image.length>0 ? newPost.image[0].url : ''
    }
    dispatch(createNotice(auth, msg, socket));

    try {
        const res = await patchAPI(`post/${post._id}/like`, newPost, auth.token);

    } catch(err) {
        dispatch({type: 'ALERT', payload: {error: err.response.data.msg}})
    }
}

export const unlikePost = (auth, post, profile, socket) => async (dispatch) => {
    // logic
    const newLikes = post.likes.filter(like => like._id !== auth.user._id)
    const newPost = {...post, likes: newLikes};
    let newPosts = profile.posts.map(p=>p._id===post._id?newPost:p);

    dispatch({type: 'UPDATE_POST', payload: newPosts});
    socket.emit('unlikePost', newPost, newPosts);

    try {
        const res = await patchAPI(`post/${post._id}/unlike`, null, auth.token);
        const msg = {
            id: auth.user._id,
            receivers: [post.user._id],
            url: post._id,
        }
        
        dispatch(deleteNotice(auth, msg))

    } catch(err) {
        dispatch({type: 'ALERT', payload: {error: err.response.data.msg}})
    }
}
