import React from 'react';
import styles from './Header.module.css';
import {AiOutlineSetting} from 'react-icons/ai';
import RoundWrapper from '../../components/util/RoundWrapper';
import NotificationCount from '../../components/NotificationCount';
import Logo from '../../assets/logo.png';
import Modal from './Modal';
import {useState,useRef,useEffect} from 'react';
import {useDispatch, useSelector} from 'react-redux';
import {CgDarkMode} from 'react-icons/cg';
import {IoExitOutline} from 'react-icons/io5';
import {RiLockPasswordLine} from 'react-icons/ri';
import {Link} from 'react-router-dom';
import { logout } from '../../store/actions/authAction'; 
import Search from './Search';
import NotificationDropdown from '../notification/NotificationDropdown';

function Header() {
    const ref = useRef();
    const dispatch = useDispatch();

    const [isShow,setIsShow] = useState({
        setting: false,
        noti: false
    });

    const auth = useSelector(state=>state.auth);

    useEffect(()=>{
        // Function to check if click outside the object
        const checkIfClickOutside = e => {
            if((isShow.setting || isShow.noti) && ref.current && !ref.current.contains(e.target)) setIsShow({setting: false,noti: false});   
        }
        // Set event 'when click' trigger
        document.addEventListener("mousedown", checkIfClickOutside)
        return () => {
            // Clean the event
            document.removeEventListener("mousedown", checkIfClickOutside)
        }
    },[isShow]);

    const showSettingToggle = () => {
        if(!isShow.setting) setIsShow({noti: false, setting: true});
        else 
        setIsShow({setting: false, noti: false});
    }

    const showNotiToggle = () => {
        if(!isShow.noti) setIsShow({setting: false, noti: true});
        else setIsShow({setting: false, noti: false});
    }

    return (
        <div className={styles.header}>
                <div className={styles.container}>
                    <Link to={'/'} >
                        <img className={styles.logo} src={Logo} alt='logo'/>
                    </Link>
                    <Search/>
                    <div className={styles.account}>
                        <div className={styles.avatar}>
                            <Link to={`/profile/${auth.user._id}`} >
                                <RoundWrapper diameter='60px'>
                                    <img src={auth.user.avatar} alt="avatar"/>
                                </RoundWrapper>
                            </Link>                          
                        </div>
                        <div ref={ref} className={styles.clickoutside_wrapper}>
                            <div  className={styles.notification_count}>
                                <NotificationCount Func={showNotiToggle} bgcolor='#5BB7ED' color='white' />
                                {isShow.noti && 
                                    <NotificationDropdown />
                                }
                            </div>          
                            <div className={styles.setting}>
                                <RoundWrapper Func={showSettingToggle} diameter='40px' bgcolor='#EAEAF1'>
                                    <AiOutlineSetting />
                                </RoundWrapper>           
                                {isShow.setting && 
                                    <Modal>
                                        <ul className={styles.list_item}>
                                            <li style={{display: 'flex'}} className={styles.item} >
                                                <div className={styles.icon_wrapper}>
                                                    <CgDarkMode className={styles.icon} />
                                                </div>  
                                                Theme mode
                                            </li>
                                            <li className={styles.item} >
                                                <Link className={styles.link}>
                                                    <div className={styles.icon_wrapper}>
                                                        <RiLockPasswordLine className={styles.icon} />
                                                    </div>  
                                                    Change Password
                                                </Link>
                                            </li>
                                            <li className={styles.item} >
                                                <Link to='/' onClick={()=>dispatch(logout())} className={styles.link}>
                                                    <div className={styles.icon_wrapper}>
                                                        <IoExitOutline className={styles.icon} />
                                                    </div>  
                                                    Logout
                                                </Link>   
                                            </li>
                                        </ul>
                                    </Modal>}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
    )
}

export default Header
