import React from 'react'
import {useState, useRef, useEffect} from 'react'
import { useSelector} from 'react-redux'
import {Link} from 'react-router-dom'

import {AiOutlineSetting} from 'react-icons/ai'

import RoundWrapper from '../../components/util/RoundWrapper'
import Search from './Search'
import NotificationDropdown from '../notification/NotificationDropdown'
import SettingOption from './SettingOption'
import NotificationCount from '../notification/NotificationCount'

import Logo from '../../assets/logo.png'
import styles from './Header.module.css'

import { searchUser } from '../../store/actions/searchAction'
import UserCard from '../util/UserCard'
import { motion, AnimatePresence } from "framer-motion"
import Spinner from '../../assets/Spinner.png'

function Header() {
    const ref = useRef();

    // const [isShow,setIsShow] = useState({
    //     setting: false,
    //     noti: false
    // });

    const [users, setUsers] = useState([]);
    const [isShow, setIsShow] = useState(false);

    const {auth, search} = useSelector(state=>state);

    useEffect(()=>{
        const checkIfClickOutside = e => {
            if((isShow.setting || isShow.noti) && ref.current && !ref.current.contains(e.target)) setIsShow({setting: false,noti: false});   
        }
        document.addEventListener("mousedown", checkIfClickOutside)
        return () => {
            document.removeEventListener("mousedown", checkIfClickOutside)
        }
    },[isShow]);

    const showSettingToggle = () => {
        if(!isShow.setting) setIsShow({noti: false, setting: true});
        else 
        setIsShow({setting: false, noti: false});
    }

    const showNotiToggle = () => {
        if(!isShow.noti) setIsShow({setting: false, noti: true});
        else setIsShow({setting: false, noti: false});
    }

    return (
        <div className={styles.header}>
            <div className={styles.container}>
                <Link to={'/'} >
                    <img className={styles.logo} src={Logo} alt='logo'/>
                </Link>
                <div>
                    <Search setIsShow={setIsShow} setUsers={setUsers} searchAction={searchUser} />
                    <AnimatePresence>
                        {isShow && 
                        <motion.div
                        initial={{maxHeight: 0, overflow: 'hidden'}}
                        animate={{maxHeight: 400, overflow: 'overlay'}}
                        transition={{duration: .5}}
                        exit={{ maxHeight: 0, overflow: 'hidden'}}
                        className={styles.results}>            
                            {search.loading ?
                            <div className={styles.spinner_wrapper}><img src={Spinner} alt='spinner' /></div>
                            :
                            users.length==0 ? 
                            <div className={styles.no_result}>No searching result...</div>
                            :
                            users.map(user => 
                                <div key={user._id} className={styles.item_wrapper}>
                                    <Link to={`/profile/${user._id}`} className={styles.item}>
                                        <UserCard height='2rem' avatar={user.avatar} username={user.username} />
                                    </Link>             
                                </div>
                            )}
                        </motion.div>}
                    </AnimatePresence>
                </div>
                <div className={styles.account}>
                    <div className={styles.avatar}>  
                        <Link to={`/profile/${auth.user._id}`} replace>
                            <RoundWrapper diameter='3.5rem'>
                            <img src={auth.user.avatar} alt="avatar"/>
                            </RoundWrapper>
                        </Link>                          
                    </div>
                    <div ref={ref} className={styles.clickoutside_wrapper}>
                        <div className={styles.notification_count}>
                            <NotificationCount Func={showNotiToggle} bgcolor='#5BB7ED' color='white' />
                            <NotificationDropdown isShow={isShow.noti} />
                        </div>          
                        <div className={styles.setting}>
                            <RoundWrapper Func={showSettingToggle} diameter='2.5rem' bgcolor='#EAEAF1'>
                            <motion.div 
                            whileHover={{rotate: 90, scale: 1.1}} 
                            style={{display: 'flex', padding: '1rem', borderRadius: '50%'}}>                    
                                <AiOutlineSetting />
                            </motion.div>
                            </RoundWrapper>           
                            <SettingOption isShow={isShow.setting}  />
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Header
