import LoginForm from "../components/auth/LoginForm";
import RegisForm from "../components/auth/RegisForm";
import styles from "./Auth.module.css";
import {useState} from 'react';

const Auth = (props) => {
    const [isLogin,setisLogin] = useState(props.authRoute);
    return (
        <>
        <div className={styles.body}>
            <div className={styles.wrapper}>
                <div className={styles.container}>
                    {/*  */}
                    <div className={`${styles.slide} ${!isLogin && styles.slide_animation}`}>
                        slide
                    </div>
                    <div className={styles.login_form_side}>
                        <div className={styles.logo}>
                            <image src={'../assets/logo.png'}></image>
                        </div>
                        <LoginForm/>
                    </div>
                    <div className={styles.regis_form_side}>
                        <RegisForm/>
                    </div>
                </div>
            </div>
        </div>
        </>
    );
}

export default Auth;
