import React from 'react';
import styles from './SlideForm.module.css';
import RoundButton from '../Util/RoundButton';
import {useDispatch,useSelector} from 'react-redux';
import {useState,useEffect} from 'react';
import {slideActions} from '../../store/index';

export default function SlideForm() {
    const BODY_DATA_1 = {
        title: 'new here?',
        text1: 'You can create here and post your experience here.',
        text2: 'Share your post increase your view.',
        valueBtn: 'Sign up'
    };

    const BODY_DATA_2 = {
        title: 'already have one?',
            text: 'Go login and check your daily posts on newfeed',
            valueBtn: 'Sign in'
    }

    const isLogin = useSelector(state => state.slide.isLogin);

    const [data,setData] = useState(BODY_DATA_1);

    const dispatch = useDispatch();

    const toggleSlide = () => {
        dispatch(slideActions.toggleSlide());
        if(!isLogin) setData(BODY_DATA_1);
        else setData(BODY_DATA_2)
    }

    return (
        <div className={`${styles.slide} ${!isLogin && styles.slide_animation}`}>
            <div className={styles.content_wrapper}>
                <div className={styles.container}>
                    <div className={styles.title}> {data.title} </div>
                    <div className={styles.text}> {data.text1} <br/> {data.text2} </div>
                    <RoundButton Func={toggleSlide}  color={'#487fec'} bgcolor={'white'} type={'text'} value={data.valueBtn} />
                </div>
            </div>
        </div>
    )
}
