import {postAPI, getAPI} from '../../apis/Axios';

export const createProject = (auth, body) => async (dispatch) => {
    try {
        dispatch({type: 'ALERT', payload: {loading: true}});
        const res = await postAPI('code', body, auth.token);
       

        dispatch({type: 'ALERT', payload: {success: res.data.msg}});

    } catch (err) {       
        dispatch({type: 'ALERT', payload: {error: err.response.data.msg}});
    }
}

export const getOwnProjects = (auth) => async (dispatch) => {
    try {
        dispatch({type: 'LOADING_PROJECT', payload: {loading: true}});
        const res = await getAPI('project', auth.token);
       
        dispatch({type: 'GET_OWN_PROJECTS', payload: res.data.codes});
        dispatch({type: 'LOADING_PROJECT', payload: {loading: false}});

    } catch (err) {       
        dispatch({type: 'ALERT', payload: {error: err.response.data.msg}});
    }
}

export const getShareProjects = (auth) => async (dispatch) => {
    try {
        dispatch({type: 'LOADING_PROJECT', payload: {loading: true}});
        const res = await getAPI('project', auth.token);
       
        dispatch({type: 'GET_SHARE_PROJECTS', payload: res.data.codes});
        dispatch({type: 'LOADING_PROJECT', payload: {loading: false}});

    } catch (err) {       
        dispatch({type: 'ALERT', payload: {error: err.response.data.msg}});
    }
}
