import LoginForm from "../components/auth/LoginForm";
import RegisForm from "../components/auth/RegisForm";
import styles from "./Auth.module.css";
import {useState} from 'react';

const Auth = (props) => {
    const [isLogin,setLogin] = useState(props.authRoute);
    return (
        <>
        <div className={styles.body}>
            <div className={styles.wrapper}>
                <div className={styles.container}>
                    {/*  */}
                    <div className={styles.slide_toggle}>
                        
                    </div>
                    <div className={styles.login_form_side}>
                    <div className={styles.logo}>
                            MEETUP APP
                        </div>
                        <h2 className={styles.title}>Đăng nhập gần đây</h2>
                        <small className={styles.note}>Nhấp vào ảnh của bạn hoặc thêm tài khoản</small>
                        {props.authRoute === 'login' && <LoginForm/>}
                        {props.authRoute === 'register' && <RegisForm/>}
                    </div>
                    <div className={styles.login_form_side}>
                        {props.authRoute === 'login' && <LoginForm/>}
                        {props.authRoute === 'register' && <RegisForm/>}
                    </div>
                </div>
            </div>
        </div>
        </>
    );
}

export default Auth;
