import styles from './NoticeOption.module.css'

const NoticeOption = () => {
    const dispatch = useDispatch();
    const {auth} = useSelector(state=>state);

    const markHandler = () => {
        dispatch(readNotice)
    }

    const deleteHandler = () => {
        dispatch()
    }
    
    return (
        <DropdownCard right={'20%'} bottom={'20%'} >
            <div className={styles.option_item}  >
                <GoCheck className={styles.icon}/>
                <div className={styles.text}>Mark read</div>
            </div>      
            <div className={styles.option_item} >
                <RiDeleteBinLine className={styles.icon}/>
                <div className={styles.text}>Delete</div>
            </div>
        </DropdownCard>
    )
}

export default NoticeOption
