import { useSelector } from 'react-redux'
import {useEffect, useState} from 'react'
import { useHistory } from 'react-router-dom'
import { useMediaQuery } from 'react-responsive'

import LoginForm from "../../components/form/LoginForm"
import RegisForm from "../../components/form/RegisForm"
import SlideForm from "../../components/form/SlideForm"

import Logo from '../../assets/logo.png'

import styles from "./Auth.module.css"


const Auth = () => {
    const history = useHistory()

    const { auth } = useSelector(state => state)

    const isDesktop = useMediaQuery({ query: '(min-width: 1224px)' })

    const [isLogin, setIsLogin] = useState(true)


    useEffect(() => {
        if(auth.token) history.push("/")

    }, [auth.token, history])
    

    return (
        <div className={styles.body}>
            <div className={styles.wrapper}>
                <div className={`${styles.container} ${isLogin ? '' : styles.flip}`}>
                    {isDesktop && <SlideForm isLogin={isLogin} setIsLogin={setIsLogin} />}
                    <div className={styles.form}>
                        <div className={styles.logo_wrapper}>
                            <img className={styles.logo} alt="logo" src={Logo}/>
                        </div>
                        <LoginForm isLogin={isLogin} setIsLogin={setIsLogin} />
                    </div>
                    <div className={styles.form2}>
                        <RegisForm isLogin={isLogin} setIsLogin={setIsLogin} />
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Auth
