import {createSlice} from '@reduxjs/toolkit';

const formSlice = createSlice({
    name:'form',
    initialState: {
        isLogin: true
    },
    reducers: {
        toggleSlide(state){
            state.isLogin = !state.isLogin;
        }
    }
});
export const formActions = formSlice.actions; 
export default formSlice.reducer;
