import React, { useEffect, useState } from 'react'
import ReactDOM from 'react-dom'

import Editor from '../../components/code/Editor'

import {SiHtml5, SiCsswizardry, SiJavascript} from 'react-icons/si'
import {AiOutlineExpandAlt} from 'react-icons/ai'
import {CgMinimizeAlt} from 'react-icons/cg'
import {HiOutlinePlusSm} from 'react-icons/hi'

import styles from './SourceCode.module.css'
import NewProjectModal from '../modal/NewProjectModal'
import Modal from '../util/Modal'

const SourceCode = () => {
    const portal = document.getElementById('portal');

    const [open, setOpen] = useState(false)
    const [isAutoSave, setAutoSave] = useState(false);
    const [isShowNewModal, setShowNewModal] = useState(false);
    const [isShowShareModal, setShowShareModal] = useState(false);

    const [html, setHtml] = useState('')
    const [css, setCss] = useState('')
    const [js, setJs] = useState('')
    const [srcDoc, setSrcDoc] = useState('');

    useEffect(() => {
        if(!isAutoSave) return;
        const timeout = setTimeout(() => {
          setSrcDoc(`
            <html>
              <body>${html}</body>
              <style>${css}</style>
              <script>${js}</script>
            </html>
          `)
        }, 500)
        return () => clearTimeout(timeout)
    }, [html, css, js, isAutoSave])

    return (
        <div className={styles.source}>
            <div className={styles.group_btn}>
                <div className={styles.btn_wrapper}>
                    <HiOutlinePlusSm/>
                    New
                </div>
                <div className={styles.btn_wrapper}>Share</div>
                <div onClick={()=>setAutoSave(!isAutoSave)} className={`${styles.btn_wrapper} ${isAutoSave?styles.autosave:''}`}>Autosave</div>
            </div>

            <div className={`${styles.editors_container} ${open ? styles.collapsed : ''}`}>
                <Editor
                language="xml"
                name="HTML"
                value={html}
                onChange={setHtml}
                labelColor='#d94924'
                icon={<SiHtml5 />}
                />
                
                <Editor
                language="css"
                name="CSS"
                value={css}
                onChange={setCss}
                labelColor='#5bb7ed'
                icon={<SiCsswizardry/>}
                />

                <Editor
                language="javascript"
                name="JS"
                value={js}
                onChange={setJs}
                labelColor='#e4d04b'
                icon={<SiJavascript/>}
                />
            </div>
            <div className={`${styles.result} ${open ? '' : styles.collapsed2}`}>
                <iframe
                srcDoc={srcDoc}
                title="output"
                sandbox="allow-scripts"
                frameBorder="0"
                width="100%"
                height="100%"
                />
                <div onClick={()=>setOpen(!open)} className={styles.expand_btn}>
                    {open ? <CgMinimizeAlt/> : <AiOutlineExpandAlt/>}
                </div>
            </div>
            {isShowNewModal && ReactDOM.createPortal(
            <Modal setIsEdit={setShowNewModal} width='30%' height='50%' >
                <NewProjectModal src={srcDoc} setIsEdit={setShowNewModal} />
            </Modal>
            , portal)}
        </div>
    )
}

export default SourceCode
