import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { follow, unfollow } from '../../store/actions/profileAction';

const FollowBtn = (props) => {
    const dispatch = useDispatch();
    const [followed, setFollowed] = useState(false);

    const auth = useSelector(state=>state.auth);
    const profile = useSelector(state=> state.profile);

    const [load, setLoad] = useState(false)

    useEffect(()=>{
        console.log(props.user)
        if(auth.user.following.find(item => item._id === props.user._id)){
            return () => setFollowed(true);
        }
        return () => setFollowed(false)

    },[auth, props.user])

    const followHandler = () => {
        if(followed) {
            dispatch(follow(auth, profile.user));
            setFollowed(true);
        }
        else {
            dispatch(unfollow(auth, profile.user));
            setFollowed(false);
        }
        
    }

    return (
        <div onClick={followHandler} >
            <div className="btn">{followed ? 'Unfollow' : 'Follow'}</div>
        </div>
    )
}

export default FollowBtn
