import React, { useEffect, useRef, useState } from 'react';
import {useDispatch, useSelector} from 'react-redux';
import styles from './Search.module.css';
import {AiOutlineSearch} from 'react-icons/ai';
import { getAPI } from '../../apis/Axios';
import { CSSTransition } from "react-transition-group";
import UserCard from './UserCard';

const Search = () => {
    const dispatch = useDispatch();
    const auth = useSelector(state=>state.auth);

    const [search,setSearch] = useState('');
    const [users,setUsers] = useState([]);

    const height = 50;

    const submitHandler = async e => {
        e.preventDefault();

        if(!search) return;
        if(auth.token && search){
            try {
                const res = await getAPI(`user/search?username=${search}`, auth.token);
                setUsers(res.data.user);

            } catch (err) {
                dispatch({type: 'ALERT', payload: {error: err.response.data.msg}})
            }   
        }
    }

    const inputHandler = e => {
        setSearch(e.target.value, ()=>console.log(search));
        if(!search) setUsers([]);
    }

    const handler = async e => {
        await inputHandler(e);
        await submitHandler(e);
    }

    return (
        <form onSubmit={submitHandler} className={styles.search_bar}>
            <div className={styles.input_wrapper}>
                <label className={styles.icon_wrapper} htmlFor="seacrh"><AiOutlineSearch className={styles.icon}/></label>
                <input onChange={handler} autoComplete="off" value={search}  className={styles.input} type="text" name="seacrh" id="seacrh" placeholder="Search for people, jobs, companies and more..."/>
            </div>
            <CSSTransition
            in={search}
            timeout={400}
            classNames={{
                enter: styles.enter,
                enterActive: styles.enter_active,
                exit: styles.exit,
                exitActive: styles.exit_active,
            }}
            unmountOnExit
            >
                <div style={{height: `${height * users.length}px`}} className={styles.results} >
                    <div className={styles.list_items}>
                        {  users.map(user => 
                            <div style={{width: '100%', height: height+'px'}} className="item">
                                <UserCard key={user.id} username={user.username} />
                            </div>    
                        )}
                    </div>
                </div> 
            </CSSTransition>
        </form>
    )
}

export default Search
