import React, { useEffect,useRef,useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import Card from '../../components/util/Card'
import RoundWrapper from '../util/RoundWrapper'
import {AiFillRightCircle} from 'react-icons/ai'
import {CgMoreAlt} from 'react-icons/cg'
import styles from './PostCard.module.css'
import ReactBar from './ReactBar'
import { updatePost } from '../../store/actions/postAction'
import PostOption from './PostOption'
import PostCounter from './PostCounter'
import Comment from './comment/Comment'
import PostContent from './PostContent'

const PostCard = ({post}) => {
    const dispatch = useDispatch();
    const ref = useRef();

    const [isEdit,setIsEdit] = useState(false);
    const [isShowOption, setShowOption] = useState(false);
    const [body,setBody] = useState();

    const {auth, profile} = useSelector(state=>state);

    useEffect(() => {
        setBody(post.body);
    }, [post.body, isEdit]);

    useEffect(()=>{
        // Function to check if click outside the object
        const checkIfClickOutside = e => {
            if(isShowOption && ref.current &&!ref.current.contains(e.target)) setShowOption(false); 
        }
        // Set event 'when click' trigger
        document.addEventListener("mousedown", checkIfClickOutside)
        return () => {
            // Clean the event
            document.removeEventListener("mousedown", checkIfClickOutside)
        }
    },[isShowOption]);

    const bodyChangeHandler = e => {
        setBody(e.target.value);
    }

    const submitHandler = e => {
        e.preventDefault();
        dispatch(updatePost(body, auth, post._id, profile));
        setIsEdit(false);
    }

    return (
        <Card width='100%' height='auto'  bg='white' mg='10% 0'>
            <div className={styles.absolute_wrapper}>
                <RoundWrapper diameter='90px' >
                    <img src={post.user.avatar} />
                </RoundWrapper>
            </div>
            {/*  */}
            <div className={styles.content_wrapper }>
                <div className={styles.content}>
                    <div className={styles.intro}>
                        <div className={styles.name}>{post.user.username}</div>
                        <div className={styles.time}>3d</div>
                    </div>
                    {isEdit ?
                    <textarea spellCheck="false" type="text" name="body" className={styles.textarea} onChange={bodyChangeHandler} value={body}/>
                    : 
                    <div className={styles.text}>{post.body}</div>
                    }
                    {isEdit &&
                    <div className={styles.update}>
                        <div onClick={()=>setIsEdit(false)} className={`${styles.cancel} ${styles.update_btn}`}>Cancel</div>
                        <div onClick={submitHandler} className={`${styles.submit} ${styles.update_btn}`}>Submit</div> 
                        </div>
                    }
                    <PostContent post={post} />
                </div>
            </div>
            <div style={{opacity: `${isShowOption ? '1' : '.5'}`}} ref={ref} onClick={()=>setShowOption(!isShowOption)} className={styles.option_btn}>
                <CgMoreAlt />
                {isShowOption && <PostOption post={post} setIsEdit={setIsEdit} />}
            </div>
            <div className="bg">
                <PostCounter post={post} />
                <ReactBar post={post} />
                <Comment />
            </div>
            
            <div className={styles.reply}>
                <div className={styles.label}>Reply</div>
                <div className={styles.icon_wrapper}><AiFillRightCircle/></div>              
            </div>
        </Card>
    )
}

export default PostCard
