import React from 'react'
import { useDispatch } from 'react-redux'

import UserCard from '../util/UserCard'
import InfiniteList from '../util/InfiniteList'

import {GrFormClose} from 'react-icons/gr'

import styles from './LikeModal.module.css'

const LikeModal = ({likes, setIsEdit}) => {
    const dispatch = useDispatch();
    
    const closeModal = () => {
        dispatch({type: 'MODAL', payload: false});
        setIsEdit(false);
    }

    return (
        <div className={styles.container}>
            <div className={styles.heading}>
                <div className={styles.title}>People like this post</div>
                <div onClick={closeModal} className={styles.close}><GrFormClose className={styles.icon} /></div>
            </div>
            <div className={styles.content}>
                {likes.map(user=>(
                    <UserCard username={user.username} avatar={user.avatar} />
                ))}
            </div>
        </div>
    )
}

export default LikeModal
