import React, { useEffect,useState } from 'react'
import ReactDOM from 'react-dom';
import {useSelector} from 'react-redux'
import { ToastContainer, toast } from 'react-toastify';
import {Toast} from './Toast';
import Loading from './Loading';
toast.configure();

function Alert() {
    const alert = useSelector(state => state.alert);
    const portal = document.getElementById('portal');

    const [alertt, setAlert] = useState({
        loading: false,
        success: '',
        error: ''
    });

    // useEffect(()=>{
    //     setAlert({
    //         loading: false,
    //         success: '',
    //         error: ''
    //     })
    // })

    useEffect(()=>{
        setAlert(alert);
    },[alert])
    
    return (
        <>
            {alertt.loading && ReactDOM.createPortal(<Loading/>,portal)}      
            {alertt.success &&  <Toast type="success" body={alertt.success} />}
            {alertt.error &&  <Toast type="error" body={alertt.error} />}
            <Toast/>
            <ToastContainer
                position="bottom-right"
                autoClose={5000}
                hideProgressBar
                newestOnTop
                closeOnClick
                rtl={false}
                pauseOnFocusLoss
                draggable
                pauseOnHover
            />
        </>
    )
}


export default Alert
