import React from 'react'
import styles from './DropdownCard.module.css'
import { motion, AnimatePresence } from "framer-motion"

const DropdownCard = (props) => {
    const variants = {
        initial: {
            maxHeight: 0,
        },
        enter: {
          maxHeight: '800px',
          transition: {
            duration: .6,
            when: "beforeChildren",
          },
        },
        exit: {
          opacity: 0,
          transition: { duration: 0.3 },
        },
      }

    return (
        <AnimatePresence >
            {props.isShow && 
            <motion.div
            variants ={variants}
            key="dog"
            initial='initial'
            animate='enter'
            exit='exit'
            style={{bottom: props.bottom, right: props.right, width: props.width}}
            className={styles.container}
            >
                {props.children}
            </motion.div>
            }
        </AnimatePresence>        
    )
}

export default DropdownCard
