import React, { useEffect, useRef, useState } from 'react'

import Header from '../../components/header/Header'

import { motion } from 'framer-motion'

import Editor from '../../components/code/Editor'

import {SiHtml5, SiCsswizardry, SiJavascript} from 'react-icons/si'

import styles from './Code.module.css'
import ProjectManager from '../../components/code/ProjectManager'


const Code = () => {
    const constRef = useRef(null);
    const [firstTab, setFirstTab] = useState(true);

    const [html, setHtml] = useState('')
    const [css, setCss] = useState('')
    const [js, setJs] = useState('')
    const [srcDoc, setSrcDoc] = useState('');

    useEffect(() => {
        const timeout = setTimeout(() => {
          setSrcDoc(`
            <html>
              <body>${html}</body>
              <style>${css}</style>
              <script>${js}</script>
            </html>
          `)
        }, 500)
        return () => clearTimeout(timeout)
    }, [html, css, js])

    return (
        <>
            <Header/>
            {/* <motion.div style={{width: 500, height:500}} ref={constRef}>
                <motion.div
                drag
                dragConstraints={constRef}
                style={{width: 'fit-content'}}
                >
                    drag me
                </motion.div>{
            </motion.div> */}
            <div className={styles.container}>
                <div>
                    <div className={styles.navigation}>
                        <div onClick={()=>setFirstTab(true)} className={`${styles.tab} ${firstTab ? styles.active:''}`}>Projects manager</div>
                        <div onClick={()=>setFirstTab(false)} className={`${styles.tab} ${!firstTab ? styles.active:''}`}>Source code</div>
                    </div>
                </div>
                {firstTab ? 
                <div className={styles.project}>
                    <ProjectManager/>
                </div>
                :
                <div className={styles.source}>
                    <div className={styles.editors_container}>
                        <Editor
                        language="xml"
                        name="HTML"
                        value={html}
                        onChange={setHtml}
                        labelColor='#d94924'
                        icon={<SiHtml5 />}
                        />
                        
                        <Editor
                        language="css"
                        name="CSS"
                        value={css}
                        onChange={setCss}
                        labelColor='#5bb7ed'
                        icon={<SiCsswizardry/>}
                        />

                        <Editor
                        language="javascript"
                        name="JS"
                        value={js}
                        onChange={setJs}
                        labelColor='#e4d04b'
                        icon={<SiJavascript/>}
                        />
                    </div>
                    <div className={styles.result}>
                        <iframe
                        srcDoc={srcDoc}
                        title="output"
                        sandbox="allow-scripts"
                        frameBorder="0"
                        width="100%"
                        height="100%"
                        />
                    </div>
                </div>
                }
            </div>
        </>
    )
}

export default Code
