const initialState = {
    loading: false,
    posts: []
}

const postReducer = (state = initialState, action) => {
    switch (action.type) {
        case 'GET_POSTS':
            return {
                ...state, 
                posts: action.payload.posts
            }
    }
}

export default postReducer;
