import React, { useState, useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'

import {MdFormatQuote, MdPhotoCamera, MdRemoveCircle} from 'react-icons/md'
import {RiShareBoxLine} from 'react-icons/ri'
import {ImAttachment} from 'react-icons/im'

import RoundWrapper from '../../components/util/RoundWrapper'
import Card from '../util/Card'

import {createPost} from '../../store/actions/postAction'

import { motion, AnimatePresence } from "framer-motion"
import TextareaAutosize from 'react-textarea-autosize'
import { useMediaQuery } from 'react-responsive'

import styles from './Status.module.css'

const Status = () => {
    const dispatch = useDispatch();

    const {auth, socket} = useSelector(state=>state);

    const isDesktop = useMediaQuery({ query: '(min-width: 1224px)' })

    const [form,setForm] = useState({
        body: '',
        image: [],
        file: []
    });

    useEffect(()=>{
        if(!form.body.trim()) document.getElementById("submit").disabled = true;
        else document.getElementById("submit").disabled = false;
    },[form.body])

    const addAttachHandler = e => {
        const files = [...e.target.files];
        const filesArr = form.image;
        let err = '';

        files.forEach(file =>{
            if(!file) return err='File does not exit';
            if(file.size > 1024*1024*25) return err='File is larger than 25mb';
            return filesArr.push(file);
        });
        if(err) dispatch({ type: 'ALERT', payload: {error: err} })
        setForm({...form, [e.target.name]: filesArr});
        e.target.value=null;
    }
    
    const removeAttachmentHandler = e => {
        const index = e.target.attributes[0].value;
        form.image.splice(index,1);
        setForm({...form});
    }

    const submitHandler = async e => {
        e.preventDefault();

        if(!form) return;
        if(auth.token && form){
            dispatch(createPost(form, auth, socket));
            setForm({
                body: '',
                image: [],
                file: []
            });
        }
    }
    return (
        <div className={styles.container}>
            <ul className={styles.list_items}>
                <li className={styles.item}>
                    <RoundWrapper fontsize="1.5rem" diameter='3rem' bgcolor='#EBB35E'>
                        <MdFormatQuote />
                    </RoundWrapper>
                    <div className={styles.text}>{`Write${isDesktop && 'a quote'}`}Write a quote</div>
                </li>
                <li className={styles.item}>
                    <RoundWrapper fontsize="1.5rem" diameter='3rem' bgcolor='#7BCDD2'>
                        <RiShareBoxLine />
                    </RoundWrapper>
                    <div className={styles.text}>Share a post</div>
                </li>
                <li className={styles.item}>
                    <RoundWrapper fontsize="1.5rem" diameter='3rem' bgcolor='#D498E9'>
                        <MdPhotoCamera />
                    </RoundWrapper>
                    <div className={styles.text}>Upload a photo</div>
                </li>
            </ul>
            <Card mg='2rem 0' width={'100%'}>
                <form onSubmit={submitHandler} action="" method="post" className={styles.form}>
                    <TextareaAutosize minRows='3' maxRows='9' value={form.body} spellCheck='false' type='text' name='body' onChange={e=>setForm({...form,body: e.target.value})} className={styles.textarea} placeholder="What's on your mind?" />
                    <div className={styles.show_images} >
                        {form.image && (
                            form.image.map((img,index)=>(
                                <motion.div
                                key={index}
                                className={styles.image_wrapper}
                                >
                                    <img  src={URL.createObjectURL(img)} alt="image" />
                                    <div onClick={removeAttachmentHandler} htmlFor={index} className={styles.remove_btn}><MdRemoveCircle className={styles.icon} /></div>
                                </motion.div>
                            ))
                        )}
                    </div>
                    <div className={styles.bottom}>
                        <div className={styles.btn_group}>
                            <div className={styles.video}>
                                <input onChange={addAttachHandler} multiple accept="*" className={styles.file_input} type="file" name="file" id="file" />
                                <label className={styles.label} htmlFor="file"><ImAttachment/></label>
                                </div>
                            <div className={styles.image}>
                                <input onChange={addAttachHandler} multiple accept="image/*" className={styles.file_input} type="file" name="image" id="image" />
                                <label className={styles.label} htmlFor="image"><MdPhotoCamera/></label>
                            </div>
                        </div>                            
                        <button id='submit' className={styles.submit} type="submit">POST►</button>
                    </div>
                </form>
            </Card>            
        </div>
    )
}

export default Status
