import React, { useState,useRef, useEffect } from 'react';
import {useDispatch, useSelector} from 'react-redux';
import styles from './Search.module.css';
import {AiOutlineSearch} from 'react-icons/ai';
import { getAPI } from '../../apis/Axios';
import { motion, AnimatePresence } from "framer-motion"

import { CSSTransition,TransitionGroup } from "react-transition-group";
import UserCard from '../util/UserCard';
import {Link} from 'react-router-dom';

const Search = () => {
    const dispatch = useDispatch();
    
    const ref = useRef();
    const searchCard = useRef(null);
    const auth = useSelector(state=>state.auth);

    const [search,setSearch] = useState('');
    const [users,setUsers] = useState([]);
    const [isShow,setIsShow] = useState(false);
    const [isLoading,setIsLoading] = useState(false);

    const variants = {
        initial: {
            maxHeight: 0,
        },
        enter: {
          maxHeight: '800px',
          transition: {
            duration: .3,
            when: "beforeChildren",
          },
        },
        exit: {
            maxHeight: 0,
          transition: { duration: 0.6 },
        }
      }


    useEffect(()=>{
        const checkIfClickOutside = e => {
            if(isShow && ref.current && !ref.current.contains(e.target)) setIsShow(false); 
        }
        document.addEventListener("mousedown", checkIfClickOutside)
        return () => {
            document.removeEventListener("mousedown", checkIfClickOutside)
        }
    },[isShow]);

    useEffect(()=>{
        if(!search) {
            setUsers([]);
            setIsShow(false);
        } else {
            submitHandler();
        }
    },[search])

    const submitHandler = async () => {
        // e.preventDefault();
        if(!search) return;
        if(auth.token && search){
            try {
                setIsLoading(true);
                const res = await getAPI(`user/search?username=${search}`, auth.token);
                setUsers(res.data.user);
                setIsShow(true);
                setIsLoading(false);

            } catch (err) {
                dispatch({type: 'ALERT', payload: {error: err.response.data.msg}})
            }   
        }
    }

    const inputHandler = e => {
        setSearch(e.target.value);
    }

    return (
        <div ref={ref}  className={styles.search_bar}>
            <div onClick={()=>setIsShow(true)} className={styles.input_wrapper}>
                <label className={styles.icon_wrapper} htmlFor="seacrh"><AiOutlineSearch className={styles.icon}/></label>
                <input spellCheck="false" onChange={inputHandler} autoComplete="off" value={search}  className={styles.input} type="text" name="seacrh" id="seacrh" placeholder="Search for people, jobs, companies and more..."/>
            </div>
            <AnimatePresence >
                {isShow && 
                <motion.div
                variants ={variants}
                key='c'
                // initial='initial'
                // animate='enter'
                animate={{height: `${searchCard.current && searchCard.current.clientHeight*users.length+65}px`, transition: .4}}
                // transition={{duration: .4}}
                // exit='exit'
                // style={{maxHeight: `${searchCard.current && searchCard.current.clientHeight*users.length+65}px`}}
                className={styles.results}
                >            
                    {users.map(user => 
                        <motion.div
                        // variants ={variants}
                        key={user._id}
                        // initial='initial'
                        // animate='enter'
                        // exit='exit'
                        className={styles.item_wrapper}
                        ref={searchCard}
                        >
                        <Link to={`/profile/${user._id}`} className={styles.item}>
                            <UserCard height='2rem' avatar={user.avatar} username={user.username} />
                        </Link>             
                        </motion.div>
                    )}
                </motion.div>
                }
            </AnimatePresence>
            {/* <CSSTransition
            in={isShow}
            timeout={500}
            appear
            classNames={{
                enter: styles.enter,
                enterActive: styles.enter_active,
                exit: styles.exit,
                exitActive: styles.exit_active
            }}
            unmountOnExit
            >
                {isLoading ? <div>loading</div>
            :     <div style={{height: `${(height+margin*2) * users.length}px`}} className={styles.results} >
            <TransitionGroup component={null}>
                {users.map(user => 
                    <CSSTransition
                        key={user._id}
                        timeout={500}
                        classNames={{
                            enter: styles.enter2,
                            enterActive: styles.enter_active2,
                            exit: styles.exit2,
                            exitActive: styles.exit_active2,
                        }}
                        unmountOnExit
                        in={isShow}
                    >
                        <div key={user._id} className={styles.item_wrapper} style={{width: '100%', height: height+'px', margin: `${margin}px 0`}} >
                            <Link to={`/profile/${user._id}`} className={styles.item}>
                                <UserCard avatar={user.avatar} username={user.username} />
                            </Link>             
                        </div>  
                    </CSSTransition>   
                )}
            </TransitionGroup>
        </div> 
            }
            
            </CSSTransition> */}
        </div>
    )
}

export default Search
