import React from 'react'
import { useDispatch } from 'react-redux'

import UserCard from '../util/UserCard'

import {GrFormClose} from 'react-icons/gr'

import styles from './LikeModal.module.css'
import FollowBtn from '../profile/FollowBtn'

const LikeModal = ({likes, setIsEdit}) => {
    const dispatch = useDispatch();
    
    const closeModal = () => {
        dispatch({type: 'MODAL', payload: false});
        setIsEdit(false);
    }

    return (
        <div className={styles.container}>
            <div className={styles.heading}>
                <div className={styles.title}>People like this post</div>
                <div onClick={closeModal} className={styles.close}><GrFormClose className={styles.icon} /></div>
            </div>
            <div className={styles.content}>
                {likes.map(user=>(
                    <div className={styles.item_wrapper}>
                        <Link className={styles.item} to={`/profile/${user._id}`}>
                            <UserCard height='3rem' color='#64BDF1' key={index} avatar={user.avatar} username={user.username} />
                        </Link>
                        <div className={styles.btn_wrapper}>
                            <FollowBtn user={user}/>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    )
}

export default LikeModal
