import { deleteAPI, patchAPI, postAPI } from "../../apis/Axios";

export const createComment = (auth, newComment, post, posts, socket) => async (dispatch) => {
    try {
        const data = {...newComment, postId: post._id, postUserId: post.user._id}
        const res = await postAPI('comment', data, auth.token);
        // logic
        const newData = {...res.data.newComment, user: auth.user};
        const newPost = {...post, comments: [...post.comments, newData]};

        let newPosts = posts.map(p=>p._id===post._id?newPost:p);

        dispatch({type: 'UPDATE_POST', payload: newPosts});
        socket.emit('commentPost', newPost, newPosts);

    } catch(err) {
        dispatch({type: 'ALERT', payload: {error: err.response.data.msg}});
    }
}

export const deleteComment = (auth, comment, post, posts) => async (dispatch) => {
    try {
        // delete the main and reply comments
        const deleteArr = [...post.comments.filter(cmt=>cmt.reply===comment._id), comment];
        // logic
        const newPost = {...post, comments: post.comments.filter(cmt=>!deleteArr.find(deletedCmt=>cmt._id===deletedCmt._id))};

        let newPosts = posts.map(p=>p._id===post._id?newPost:p);

        dispatch({type: 'UPDATE_POST', payload: newPosts});

        deleteArr.forEach(item=>{
            deleteAPI(`comment/${item._id}`, auth.token);
        })

    } catch(err) {
        dispatch({type: 'ALERT', payload: {error: err.response.data.msg}});
    }
}

export const updateComment = (auth, body, post, posts, comment) => (dispatch) => {
    const newComment = {...comment, body: body};
    const newComments = post.comments.map(cmt=>cmt._id===comment._id?newComment:cmt);

    const newPost = {...post, comments: newComments};
    
    let newPosts=posts.map(p=>p._id===post._id?newPost:p);
    
    dispatch({type: 'UPDATE_POST', payload: newPosts});
    try {
        const res = patchAPI(`comment/${comment._id}`, {body}, auth.token);

    } catch(err) {
        dispatch({type: 'ALERT', payload: {error: err.response.data.msg}});
    }
}
