import './App.css';
import React, {useEffect} from 'react'
import {useSelector, useDispatch} from 'react-redux'
import {BrowserRouter as Router, Route, Switch} from 'react-router-dom'
import ReactDOM from 'react-dom'

import Auth from './views/Auth/Auth'
import Home from './views/Home/Home'
import { PageRender } from './components/customRouter/PageRender'
import ProtectedRoute from './components/customRouter/ProtectedRoute'

import axios from 'axios';

import Alert from './components/alert/Alert'
import NotificationDisplay from './components/notification/NotificationDisplay'

import io from 'socket.io-client'
import SocketClient from './SocketClient'

import { getNotices } from './store/actions/noticeAction'
import {refreshToken} from './store/actions/authAction'

function App() {
  const dispatch = useDispatch();
  const alert = document.getElementById('alert');
  const notification = document.getElementById('notification');
  const  {auth, modal}  = useSelector(state => state);

  useEffect(()=>{
    if(modal) document.getElementById('root').style.filter = 'blur(3px)'
    else document.getElementById('root').style.filter = 'blur(0px)'
  })

  useEffect(()=>{
    dispatch(refreshToken());
    const socket = io();
    dispatch({type: 'SOCKET', payload: socket});
    return ()=> socket.close();
  },[dispatch]);

  useEffect(()=>{
    if(auth.token) {
      dispatch(getNotices(auth));
    }
  },[dispatch, auth.token])

  useEffect(()=>{
    // axios.interceptors.request.use(request=>{
    //   request.headers.authtoken = auth.token;
    //   console.log(request); 
    //   return request;
    // }, error=>{
    //   console.log(error);
    //   return Promise.reject(error)
    // })

    axios.interceptors.response.use(undefined, error=>{
      const {status, data, config} = error.response;
      if(status === 404) {
        console.log('not found')
      }
      if(status === 500) {
        console.log('server error')
      }

      return Promise.reject(error)
    })
  },[auth.token])

  return (
  <Router>
    {ReactDOM.createPortal(<Alert />, alert)}
    {ReactDOM.createPortal(<NotificationDisplay/>, notification)}

    {auth.token && <SocketClient/>}
    <Switch>
      <Route exact path='/' component={auth.token ? Home : Auth} />
      <ProtectedRoute exact path='/:page' component={PageRender} />
      <ProtectedRoute exact path='/:page/:id' component={PageRender} />
    </Switch>
  </Router>
  )

}
export default App;
