import React, {useEffect} from 'react'
import { useDispatch, useSelector } from 'react-redux'
import {Link} from 'react-router-dom'

import {getSuggestion} from '../../../store/actions/suggestionAction'

import UserCard from '../../util/UserCard'
import FollowBtn from '../../profile/FollowBtn'

import styles from './FriendSuggestion.module.css'

const FriendSuggestion = () => {
    const dispatch = useDispatch();
    const {auth, suggestion} = useSelector(state=>state);
    
    return (
        <div className={styles.container}>
            {suggestion.users.map((user,index)=>(
                <div className={styles.item_wrapper}>
                    <Link className={styles.item} to={`/profile/${user._id}`}>
                        <UserCard color={'#5bb7ed'} key={index} avatar={user.avatar} username={user.username} />
                    </Link>
                    <div className={styles.btn_wrapper}>
                        <FollowBtn user={user}/>
                    </div>
                </div>
            ))}
            
        </div>
    )
}

export default FriendSuggestion
