import React, { useEffect, useState } from 'react'
import RoundWrapper from '../util/RoundWrapper'
import { useSelector } from 'react-redux'

export default function NotificationCount(props) {
    const {auth, notice} = useSelector(state=>state);
    const [notiLength, setNotiLength] = useState();

    useEffect(()=>{
        const readNotices = notice.data.filter(noti=>noti.isRead.every(id=>id!=auth.user._id));
        setNotiLength(readNotices.length)
    },[notice])

    return (
            <RoundWrapper Func={props.Func} margin={props.margin} diameter={props.diameter} bgcolor={props.bgcolor} color={props.color}>
                {notiLength}
            </RoundWrapper>
    )
}
