import { deleteAPI, postAPI } from "../../apis/Axios";

export const createComment = (auth, newComment, post) => async (dispatch) => {
    // logic
    const newPost = {...post, comments: [...post.comments, newComment]};
    // let newPosts = [];

    // for (let i = 0; i<post.length; i++) {
    //     if(post[i]._id === post._id) newPosts.push(newPost);
    //     else newPosts.push(post[i]);
    // }
    dispatch({type: 'UPDATE_POST', payload: newPost});

    try {
        const data = {...newComment, postId: post._id, postUserId: post.user._id}
        const res = await postAPI('comment', data, auth.token);
        console.log(res);

    } catch(err) {
        dispatch({type: 'ALERT', payload: {error: err.response.data.msg}});
    }
}

export const deleteComment = (auth, comment, post) => async (dispatch) => {
    try {
        // delete the main and reply comments
        const deleteArr = [...post.comments.filter(cmt=>cmt.reply===comment._id), comment];

        deleteArr.forEach(item=>{
            deleteAPI(`comment/${item._id}`, auth.token);
        })

    } catch(err) {
        dispatch({type: 'ALERT', payload: {error: err.response.data.msg}});
    }
}
