import React from 'react';
// import NotificationCount from '../NotificationCount';
import styles from './NavItem.module.css';
import { useState } from 'react';

export default function NavItem(props) {
    const [isHover,setIsHover] = useState(false);

    const mouseEnterHandler = () => {
        setIsHover(true);
    }

    const mouseLeaveHandler = () => {
        setIsHover(false);
    }


    return (
        <div className={styles.nav_item} onMouseEnter={mouseEnterHandler} onMouseLeave={mouseLeaveHandler} >
            <div className={styles.icon_wrapper}>
                {props.children}
            </div>
            <div className={styles.text}>{props.text}</div>
            <div className={styles.noti_counter}>
                {/* {isHover && <NotificationCount bgcolor='#A7B1B7' color='white' />}
                {!isHover && <NotificationCount bgcolor='white' color='#A7B1B7' />} */}
            </div>
            
        </div>
    )
}
