import React from 'react'
import { useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import PageNotFound from '../../views/PageNotFound/PageNotFound';
import axios from 'axios';


const generatePage = (pageName,id) => {
    const page = pageName.charAt(0).toUpperCase() + pageName.slice(1);
    const  component = () => require(`../../views/${page}/${page}`).default;

    const err = errorHandler();
    if(err) console.log(err);
    try {
        return React.createElement(component());

    } catch (err) {
        return <PageNotFound/>
    }
}

const errorHandler = () => {
    return axios.interceptors.response.use(undefined, error=>{
        const {status, data, config} = error.response;
        if(status === 404) {
            throw new Error(` not found`);
        }
        if(status === 500) {
          console.log('server error')
        }
        
        return Promise.reject(error)
    })
}

export const PageRender = () => {
    const {page,id} = useParams();
    const {auth} = useSelector(state=>state);

    let pageName = '';
    if(auth.token) pageName = `${page}`;



    return (
        generatePage(pageName,id)  
    )
}
